package week1.day1;

public class PrimeNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int input=10126;
		int count=0;
		for(int i=1;i<=input;i++) {
			if(input%i==0) {
				count++;
			}}
				if(count==2) 
				{
					System.out.println(input + "is  a prime number");
				}
				
				else{
					System.out.println(input + "is NOT  a prime number");
				}
			}
		}
		

	


